import React, { useState } from "react";
import InputField from "../Fields/Field.component";
import "./registrationForm.style.css";

const AddProductForm = () => {
  const [inputValue, setInputValue] = useState({ name: "", price: "" });
  const { name, price } = inputValue;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputValue((prev) => ({
      ...prev,
      [name]: value,
    }));
    console.log(inputValue);
  };

  const formHeader = () => {
    return (
      <div>
        <h2>Sign up</h2>
        <p>Already have an account? <a>Sign in</a></p>
      </div>
    )
  }

  return (
    <>
    <form className="form-container">
      <div className="form-wrapper">
        <div className="form-header">
          <InputField
            type="text"
            value={name}
            placeholder=""
            label="Suffix"
            name="Suffix"
            className="suffix inputField-header"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={price}
            placeholder=""
            label="Title"
            name="Title"
            className="title inputField-header"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={name}
            placeholder=""
            label="Firstname"
            name="Firstname"
            className="firstname inputField-header"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={price}
            placeholder=""
            label="Lastname"
            name="Lastname"
            className="lastname inputField-header"
            onChange={handleChange}
          />
        </div>
        <div className="form-body">
          <div className="email-box">
            <InputField
              type="email"
              value={name}
              placeholder=""
              label="Email"
              name="Email"
              className="email inputField-body"
              onChange={handleChange}
            />
            <p className="email-description">
              Click the checkbox to use email us predefined sign in option
            </p>
          </div>
          <InputField
            type="text"
            value={price}
            placeholder=""
            label="Username"
            name="Username"
            className="userName inputField-body"
            onChange={handleChange}
          />
        </div>
        <div className="form-bottom">
          <InputField
            type="text"
            value={name}
            placeholder=""
            label="Country Code"
            name="countryCode"
            className="countryCode inputField-bottom"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={price}
            placeholder=""
            label="Phone Number"
            name="phoneNumber"
            className="phoneNumber inputField-bottom"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={name}
            placeholder=""
            label="Password"
            name="password"
            className="password inputField-bottom"
            onChange={handleChange}
          />
          <InputField
            type="text"
            value={price}
            placeholder=""
            label="Confirm Password"
            name="confirmPassword"
            className="confirmPassword inputField-bottom"
            onChange={handleChange}
          />
        </div>
      </div>
    </form>
    </>
  );
};

export default AddProductForm;
