import React, { useState } from "react";
import InputField from "../../../Component/Fields";
import { visibilityIcon, visibilityOffIcon } from "../../../Shared/Icons";
import { Values } from "../../../Constants";
import "./signInForm.style.css"

import Box from "@mui/material/Box";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select, { SelectChangeEvent } from "@mui/material/Select";

const SignInForm = () => {
  let initialState = [
    {
      suffix: "",
      title: "",
      firstName: "",
      lastName: "",
      email: "",
      userName: "",
      countryCode: "",
      phoneNumber: "",
      password: "",
      confirmPassword: "",
    },
  ];
  const [inputValue, setInputValue] = useState(initialState);
  let [isDisabled, setDisabled] = useState(true)
  const {
    suffix,
    title,
    firstName,
    lastName,
    email,
    userName,
    countryCode,
    phoneNumber,
    password,
    confirmPassword,
  } = inputValue;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputValue((prev) => ({
      ...prev,
      [name]: value,
    }));
    console.log(inputValue);
  };

  const formHeader = () => {
    return (
      <div className="header-container">
        <h2>{Values.SignIn}</h2>
        <span className="header-content">
          {Values.ExistingCustomer} <a href="">{Values.SignUp}</a>
        </span>
      </div>
    );
  };

  const socialLogin = () => {
    return (
      <div className="socialLogin-container">
        <InputField
          type="button"
          value={Values.GoogleLogin}
          placeholder=""
          className="google-login"
        />
        <InputField
          type="button"
          value={Values.FaceBookLogin}
          placeholder=""
          className="google-login"
        />
        <InputField
          type="button"
          value={Values.GitHubLogin}
          placeholder=""
          className="github-login"
        />
      </div>
    );
  };

  return (
    <>
      <form className="form-container">
        {formHeader()}
        <div className="form-wrapper">
          <div className="form-body">
            <div className="email-wrapper">

              <InputField
                type="email"
                value={email}
                placeholder={Values.Email}
                label={Values.Email}
                name={Values.Email}
                className="inputField-body"
                required={true}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="form-bottom">
            <InputField
              type="text"
              value={countryCode}
              placeholder=""
              label={Values.CountryCode}
              name="countryCode"
              className="inputField-bottom"
              required={true}
              onChange={handleChange}
            />
            <div className="code-wrapper">
              <Box
                sx={{ m: 1, flexBasis: "24%", margin: "1rem 0rem 0rem 0rem" }}
                size="small"
              >
                <FormControl fullWidth>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={countryCode}
                    label=""
                    onChange={handleChange}
                  >
                    <MenuItem value={+91}>+91</MenuItem>
                    <MenuItem value={+862}>+862</MenuItem>
                    <MenuItem value={+99}>+99</MenuItem>
                    <MenuItem value={+7432}>+7432</MenuItem>
                  </Select>
                </FormControl>
              </Box>
              <div className="phone-wrapper">
                <InputField
                  type="text"
                  value={phoneNumber}
                  placeholder={Values.PhoneNumber}
                  label={Values.PhoneNumber}
                  name="phoneNumber"
                  className="inputField-bottom"
                  onChange={handleChange}
                />
                <InputField
                  type="checkbox"
                  name={Values.Email}
                  className="inputField-checkbox"
                  onChange={handleChange}
                />
                <p className="phone-description">
                  {Values.PholePredefinedOption}
                </p>
              </div>
            </div>
            <div className="password-wrapper">
              <InputField
                type="password"
                value={password}
                placeholder={Values.PasswordPlaceholder}
                label={Values.Password}
                name="password"
                className="inputField-bottom"
                required={true}
                onChange={handleChange}
              />
              <i className="visibility-icon">{visibilityOffIcon()}</i>
            </div>
            <div className="password-wrapper">
              <InputField
                type="password"
                value={confirmPassword}
                placeholder={Values.RepeatPassowrdPlaceholder}
                label={Values.ConfirmPassword}
                name="confirmPassword"
                className="inputField-bottom"
                required={true}
                onChange={handleChange}
              />
              <i className="visibility-icon">{visibilityOffIcon()}</i>
            </div>
          </div>
          <div className="btn-submit">
            <button type="submit" disabled={isDisabled} className={isDisabled ? "disabled-button": "form-submit"}>{Values.CreateAccount}</button>
          </div>
          <div className="lines">
            <div className="line-left"></div>
            <span>OR</span>
            <div className="line-right"></div>
          </div>
          {socialLogin()}
        </div>
      </form>
    </>
  );
};

export default SignInForm;
