import React, { useState } from "react";
import InputField from "../Fields/Field.component";
import './registrationForm.st'

const AddProductForm = () => {
  const [inputValue, setInputValue] = useState({ name: "", price: "" });
  const { name, price } = inputValue;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputValue((prev) => ({
      ...prev,
      [name]: value,
    }));
    console.log(inputValue);
  };

  return (
    <form className="registration-form">
      <div className="form-top">
        <InputField
          type="text"
          value={name}
          placeholder=""
          label="Suffix"
          name="Suffix"
          onChange={handleChange}
        />
        <InputField
          type="number"
          value={price}
          placeholder=""
          label="Title"
          name="Title"
          onChange={handleChange}
        />
         <InputField
          type="text"
          value={name}
          placeholder=""
          label="Firstname"
          name="Firstname"
          onChange={handleChange}
        />
        <InputField
          type="number"
          value={price}
          placeholder=""
          label="Lastname"
          name="Lastname"
          onChange={handleChange}
        />
      </div>
    </form>
  );
};

export default AddProductForm;
